<?php

namespace Tests\Feature\Http\Controllers;

use App\Mail\MemberAppInterviewConfirmation;
use App\Models\User;
use Database\Seeders\FormsTableSeeder;
use Illuminate\Foundation\Testing\RefreshDatabase;
use Illuminate\Foundation\Testing\WithoutMiddleware;
use Illuminate\Mail\MailManager;
use Illuminate\Support\Facades\Mail;
use Illuminate\Testing\TestResponse;
use Tests\TestCase;

class FormsControllerTest extends TestCase
{
    use RefreshDatabase, WithoutMiddleware;

    private MailManager $mailerOriginal;

    public function submitApplication(User $adminUser, $overrides = []): TestResponse
    {
        // upload photo
        $image = $this->actingAs($adminUser)->post('/image-crop/users', [
            'photo_type' => 'users',
            'id' => 'new',
            'image' => 'data:image/png;base64,',
        ]);

        return $this->actingAs($adminUser)->post('/forms/1/save',
            array_merge([
                'form_id' => 1,
                'returning_user' => $adminUser->id,
                'element-cdf75849-ccb8-4cdf' => 'i',
                'first_name' => 'new',
                'last_name' => 'user',
                'email' => 'new@user.com',
                'phone' => '(123) 456-7890',
                'address' => 'some address',
                'city' => 'some city',
                'province' => 'AB',
                'postal' => 'A1A 1A1',
                'photo' => $image->json('filename'),
            ],
                $overrides));
    }

    protected function setUp(): void
    {
        parent::setUp();
        $this->seed(FormsTableSeeder::class);

        $this->mailerOriginal = Mail::getFacadeRoot();
        Mail::fake();
    }

    /** @test */
    public function it_sends_a_confirmation_email_to_applicant_when_application_is_submitted()
    {
        $submittingUser = User::factory()->active()->create();

        $response = $this->submitApplication($submittingUser, [
            'first_name' => 'first',
            'last_name' => 'last',
            'email' => 'new@user.com',
        ]);

        $newUser = User::where('email', 'new@user.com')->first();

        $response->assertStatus(302);

        Mail::assertSent(MemberAppInterviewConfirmation::class, function (MemberAppInterviewConfirmation $mail) use ($newUser) {
            Mail::swap($this->mailerOriginal);
            $mail->assertTo($newUser->email);
            $mail->assertFrom('membership@kwartzlab.ca');
            $mail->assertHasSubject('Application Received - Kwartzlab Makerspace');
            $mail->assertSeeInOrderInHtml([
                $newUser->get_name(),
                config('kwartzlabos.membership_coordinator.name'),
                'Membership Coordinator',
                'membership@kwartzlab.ca',
            ]);

            return true;
        });
    }
}
